
public class A1H4 {
//Write a program to find factorial of a number
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int fact=1;
		int num=6;
		for (int i = 1; i <=num; i++)
		{
			fact=fact*i;
		}
		System.out.println("factorial of "+num+" is: "+fact);
	}

}
