
public class A1H7 {
//Write a program to display even and odd numbers between a range entered by the user. (Variations)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int start=100;
		int end=200;
		for(int i=start;i<=end;i++)
		{
			if(i%2==0)
			{
				System.out.println(i+"-even");
			}
			else
			{
				System.out.println(i+"-odd");
			}
		}

	}

}
